catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    buscarInformacaoEndereco('buscarCidade', 'city');
    buscarInformacaoEndereco('buscarEstado', 'estate');
    buscarInformacaoEndereco('buscarBairro', 'neigh');

    function buscarInformacaoEndereco(metodo, variavel) {
        var gla = new GlideAjax('CepAPIajax');
        gla.addParam('sysparm_name', metodo);
        gla.addParam('sysparm_cep', newValue);
        gla.getXML(callback);

        function callback(response) {
            var answer = response.responseXML.documentElement.getAttribute('answer');

            if (answer) {
                g_form.setValue(variavel, answer);
            }
        }
    }

}
