catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (newValue == '') {
		g_form.clearValue('email');
        return;
    }

    //Type appropriate comment here, and begin script below
    if (newValue) {
		gerarGlideAjax('findEmail', 'email');
	}

	function gerarGlideAjax(metodo, value){
        var ga = new GlideAjax('CatalogItemUtilsAjax');
        ga.addParam('sysparm_name', metodo);
        ga.addParam('sysparm_user_sys_id', newValue);
        ga.getXML(callbackFunction);

        function callbackFunction(response) {
            var answer = response.responseXML.documentElement.getAttribute("answer");
			if(answer){
				g_form.clearValue(value);
				g_form.setValue(value, answer);
			}
        }
	}
}
